import React, { useState } from "react";
import {
    Chart as ChartJS,
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Tooltip,
    Title,
    Legend
} from "chart.js";
import { Line } from "react-chartjs-2";
import { dataNew } from './data';
import moment from "moment";

ChartJS.register(
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend
  );

export default function Chart() {
    const [filterData, setFilterData] = useState(dataNew);

    const options = {
        responsive: true,
        plugins: {
          legend: {
          },
          title: {
            display: true,
            text: "Chart.js Line Chart"
          }
        }
      };
      const labels = filterData.map((data) => {
        return data.Date;
      });

       const data = {
        labels,
        datasets: [
          {
            label: "Volume",
            data: filterData.map((data) => {
              return data.Volume;
            }),
            borderColor: "rgb(255, 99, 132)",
            backgroundColor: "rgba(255, 99, 132, 0.5)"
          },
          {
            label: "Adj Close",
            data: filterData.map((data) => {
              return data["Adj Close"];
            }),
            borderColor: 'rgba(54, 162, 235, 1)',
            backgroundColor: 'rgba(54, 162, 235, 1)',
          }
        ]
      };

     const  handleDay = () =>{
      const today = new Date();
      const date = (today.getMonth()+1)+'/'+today.getDate()+'/'+today.getFullYear();
      const newDate= moment(date).format('MM/DD/YYYY');
      const filterDataValue =dataNew.filter((result)=>{
    if (result.Date == newDate){
      return result;
    }
  })
  setFilterData(filterDataValue);
    } 
    const  handleWeek = ( ) =>{
      var curr = new Date;
      var firstday = new Date(curr.setDate(curr.getDate() - curr.getDay()));
      var lastday = new Date(curr.setDate(curr.getDate() - curr.getDay()+6));
      const firstdayNew= moment(firstday).format('MM/DD/YYYY');
      const lastdayNew= moment(lastday).format('MM/DD/YYYY');

      const filterDataValue =dataNew.filter((result)=>{


        var dateFrom = firstdayNew;
        var dateTo = lastdayNew;
        var dateCheck = result.Date;

        var d1 = dateFrom.split("/");
        var d2 = dateTo.split("/");
        var c = dateCheck.split("/");

        var from = new Date(d1[2], parseInt(d1[1])-1, d1[0]);  // -1 because months are from 0 to 11
        var to   = new Date(d2[2], parseInt(d2[1])-1, d2[0]);
        var check = new Date(c[2], parseInt(c[1])-1, c[0]);

        if (check > from && check < to) {
          return result;
        }

      });

      setFilterData(filterDataValue);


    }


      return (
          <>
        <Line options={options} data={data} />
        <button onClick={() => handleDay()} > 1 day</button>
        <button onClick={() => handleWeek()} > 1 week</button>

        <button onClick={() => setFilterData(dataNew)} >Refresh</button>
          </>
      )



}
