import React, { useState, useEffect } from "react";
import {
    Chart as ChartJS,
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Tooltip,
    Title,
    Legend
} from "chart.js";
import { Line } from "react-chartjs-2";
import { dataNew } from './data';
import moment from "moment";

ChartJS.register(
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend
  );

export default function Chart() {
    const [filterData, setFilterData] = useState(dataNew);
    const [newData, setNewData] = useState([]);
    const [dateCounter,setDateCounter] = useState(1);
    const slot =["1992-05-02",'1998-02-03','2004-21-09',"2009-02-10","2022-04-01"];


    const DataSlicer=(counter,slot,data)=>{
        let startValue = new Date(dataSlot[0]);
        let endValue = new Date(slot[counter]);
        if(startValue && endValue){
            return data;

        }else{
            return [];
        }
    }
    setNewData(DataSlicer);

    console.log("data slicer", DataSlicer)

    const options = {
        responsive: true,
        plugins: {
          legend: {
          },
          title: {
            display: true,
            text: "Chart.js Line Chart"
          }
        }
      };
      const labels = newData.map((data) => {
        return data.Date;
      });

       const data = {
        labels,
        datasets: [
          {
            label: "Volume",
            data: newData.map((data) => {
              return data.Volume;
            }),
            borderColor: "rgb(255, 99, 132)",
            backgroundColor: "rgba(255, 99, 132, 0.5)"
          },
          {
            label: "Adj Close",
            data: newData.map((data) => {
              return data["Adj Close"];
            }),
            borderColor: 'rgba(54, 162, 235, 1)',
            backgroundColor: 'rgba(54, 162, 235, 1)',
          }
        ],
        scales: {
            x: {
             suggestedMin: 0,
             suggestedMax: 69
            }
        }
      };



      return (<Line options={options} data={data} />);



}
